clear all;    % clear all variablesclose all;    % close all figuresclc;          % clear command window  % import image packagepkg load image; % read imageimg = imread("lena.png");  % convert image into gray and then from uint8 to doublegry = double(rgb2gray(img)); % low pass filter%myfilter = (1/49)*ones(7,7);% high pass filtermyfilter = [-1,2,-1];% 3x3 high pass filter%myfilter = [-1 -2 -1;0 0 0; 1 2 1];% apply filter on gray imageresult = imfilter(gry, myfilter);% median filter%result = medfilt2(gry, 5);% plot original gray imagefiguresubplot(1,2,1);imshow(uint8(gry));% plot filtered gray imagesubplot(1,2,2);imshow(uint8(result));