clear all;clc;# Original Imageref = rgb2gray(imread('lena.png'));# Add noise noisy = imnoise(ref, 'salt & pepper', 0.2);# in-built psnr function%psnr = psnr(ref);# MSE implementation[M N] = size(noisy);diff = (double(ref) - double(noisy)) .^ 2;err = sum(sum(diff)) / (M*N);# psnr implementationpsnr = 10*log10(256^2 / err);figure; imshow(ref); title("Original Image");figure; imshow(noisy); title("Noisy Image");fprintf('\n The psnr is %0.4f\n', psnr);